// src/components/RightAbove.js
import React, { useEffect, useState } from 'react'
import Question from './Question' // Import the Question component

function RightAbove() {
  const [questions, setQuestions] = useState([])
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0)

  useEffect(() => {
    const fetchQuestions = async () => {
      try {
        const response = await fetch('/data/townQuestions.json') // Fetch data from the JSON file
        const data = await response.json()
        setQuestions(data.townQuestions)
      } catch (error) {
        console.error('Error fetching questions:', error)
      }
    }

    fetchQuestions()
  }, [])

  const handleNextQuestion = () => {
    setCurrentQuestionIndex((prevIndex) => (prevIndex + 1) % questions.length) // Loop back to start if at the end
  }

  return (
    <div className="border-2 border-black h-full bg-green-800 overflow-y-auto h-{100%}">
      <div className="m-8 p-2 border-2 border-black bg-amber-950">
        <div className="m-1 p-4 border-1 border-black bg-white">
          <p className="text-sm">VILLAGE BULLETIN</p>
          {questions.length > 0 ? (
            <Question
              question={questions[currentQuestionIndex]}
              onNext={handleNextQuestion}
            />
          ) : (
            <p>Loading questions...</p>
          )}
        </div>
      </div>
    </div>
  )
}

export default RightAbove
