import React from 'react'

function HintBit({ hint, onToggleHint, showHint }) {
  return (
    <div className="p-4 bg-amber-200 h-[324px] overflow-y-auto flex gap-4">
      <div className="m-1 p-4 h-1/2 bg-white rounded-xl">
        <p>
          Hi! I'm Remy the Raccoon. I know these trees pretty well. Click me for
          a hint!
        </p>
        {/* Display the hint if showHint is true */}
      </div>
      <button
        onClick={onToggleHint}
        // className="mt-2 p-2 bg-green-500 text-white"
      >
        {showHint ? 'Hide Hint' : 'Show Hint'}
        <img src="/img/1-tree/raccoon.PNG" alt="" className="w-[200px]" />
      </button>
      {showHint && <p className="mt-2">{hint}</p>}{' '}
    </div>
  )
}

export default HintBit
