import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Route, Switch, Link } from 'react-router-dom'; // Import necessary components from react-router-dom
import logo from './logo.svg';
import './App.css';
import CreateProfile from './createprofile'; // Import the CreateProfile component

function App() {
  const [members, setMembers] = useState([]);

  useEffect(() => {
    // Fetch the members from the Flask backend
    fetch('/members') // Ensure this URL points to your Flask backend
      .then(response => response.json())
      .then(data => setMembers(data.members))
      .catch(error => console.error('Error fetching members:', error));
  }, []);

  return (
    <Router>
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <p>
            <code>Hello Sweetcoders...Happy Hacking! <span>ðŸ˜Š</span></code>
          </p>
        </header>
        <nav>
          <Link to="/">Home</Link> {/* Link to the home page */}
          <Link to="/createprofile">Create Profile</Link> {/* Link to the Create Profile page */}
        </nav>
        <Switch>
          <Route exact path="/">
            <h3>Here is the list of our members</h3>
            <ul>
              {members.map((member, index) => (
                <li key={index}>{member}</li>
              ))}
            </ul>
          </Route>
          <Route path="/createprofile">
            <CreateProfile /> {/* Render the CreateProfile component when the route matches */}
          </Route>
        </Switch>
      </div>
    </Router>
  );
}

export default App;
