import React, { useState } from 'react';

const InterviewIsle = () => {
  const [question, setQuestion] = useState('');
  const [userInput, setUserInput] = useState('');
  const [response, setResponse] = useState('');

  const handleQuestionChange = (e) => {
    setQuestion(e.target.value);
  };

  const handleInputChange = (e) => {
    setUserInput(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch('/interview', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ question, userInput }),
      });

      if (res.ok) {
        const data = await res.json();
        setResponse(data.content); // Assuming the backend returns { content: ... }
      } else {
        setResponse('Error: Could not fetch response from server.');
      }
    } catch (error) {
      console.error('Error:', error);
      setResponse('Error: Something went wrong.');
    }
  };

  return (
    <div className="p-4">
      <h1 className="text-2xl font-bold">Interview Isle</h1>
      <form onSubmit={handleSubmit} className="mt-4">
        <div className="mb-4">
          <label className="block text-sm font-medium text-gray-700" htmlFor="question">
            Question
          </label>
          <input
            type="text"
            id="question"
            value={question}
            onChange={handleQuestionChange}
            className="mt-1 p-2 border border-gray-300 rounded-md w-full"
            required
          />
        </div>
        <div className="mb-4">
          <label className="block text-sm font-medium text-gray-700" htmlFor="userInput">
            Your Answer
          </label>
          <textarea
            id="userInput"
            value={userInput}
            onChange={handleInputChange}
            className="mt-1 p-2 border border-gray-300 rounded-md w-full h-24"
            required
          />
        </div>
        <button
          type="submit"
          className="mt-4 bg-blue-500 text-white p-2 rounded-md"
        >
          Submit
        </button>
      </form>
      {response && (
        <div className="mt-4 p-2 border border-gray-300 rounded-md">
          <h2 className="font-semibold">Response:</h2>
          <p>{response}</p>
        </div>
      )}
    </div>
  );
};

export default InterviewIsle;
