import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const CreateProfile = () => {
  const [nickname, setName] = useState('');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Add your logic to send the profile data to the backend
    const response = await fetch('/createprofile', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ name, username, password }),
    });

    if (response.ok) {
      // Handle successful profile creation (e.g., redirect to dashboard)
      navigate('/view_dashboard'); // Redirecting to the dashboard
    } else {
      const errorData = await response.json();
      setErrorMessage(errorData.message || 'Failed to create profile');
    }
  };

  return (
    <div className="phone-container">
      <div className="container">
        <div className="title">Create Profile</div>

        {errorMessage && <div className="alert alert-danger">{errorMessage}</div>}

        <form onSubmit={handleSubmit}>
          <input
            type="text"
            className="input"
            name="name"
            placeholder="Nickname"
            value={nickname}
            onChange={(e) => setName(e.target.value)}
            required
          />
          <input
            type="text"
            className="input"
            name="username"
            placeholder="Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
          <input
            type="password"
            className="input"
            name="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <button
            className="button2"
            type="button"
            onClick={() => navigate('/')} // Navigate back to home or desired route
          >
            Back
          </button>
          <button className="button2" type="submit">
            Save
          </button>
        </form>
      </div>
    </div>
  );
};

export default CreateProfile;
