// src/components/RightAbove.js
import React, { useEffect, useState } from 'react'
import Question from './Question' // Import the Question component

function RightAbove() {
  const [questions, setQuestions] = useState([])
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0)

  const villagerNames = [
    'BUBBLES THE BUNNY',
    'MELVIN THE MOOSE',
    'FELICITY THE FOX',
    'NEDDY THE NARWHAL',
    'PIPPIN THE PENGUIN',
    'SILLY SAMMY THE SLOTH',
    'LUCY THE LEMUR',
    'DIZZY THE DOLPHIN',
    'GIGGLES THE GIRAFFE',
    'WOBBLES THE WOMBAT',
  ]

  useEffect(() => {
    const fetchQuestions = async () => {
      try {
        const response = await fetch('/data/townQuestions.json') // Fetch data from the JSON file
        const data = await response.json()
        setQuestions(data.townQuestions)
      } catch (error) {
        console.error('Error fetching questions:', error)
      }
    }

    fetchQuestions()
  }, [])

  const handleNextQuestion = () => {
    setCurrentQuestionIndex((prevIndex) => (prevIndex + 1) % questions.length) // Loop back to start if at the end
  }

  const getRandomName = () => {
    const randomIndex = Math.floor(Math.random() * villagerNames.length)
    return villagerNames[randomIndex]
  }

  const randomVillagerName = getRandomName()

  return (
    <div className="border-2 border-black bg-green-800 h-[356px] overflow-y-auto ">
      <div className="m-6 p-2 border-2 border-black bg-amber-950">
        <div className="p-4 border-1 border-black bg-white">
          <div className="flex justify-between">
            <p className="text-xs">{randomVillagerName} ASKED: </p>
            <p className="text-xs"> VILLAGER BULLETIN</p>
          </div>

          {questions.length > 0 ? (
            <Question
              question={questions[currentQuestionIndex]}
              onNext={handleNextQuestion}
            />
          ) : (
            <p>Loading questions...</p>
          )}
        </div>
      </div>
    </div>
  )
}

export default RightAbove
